comments.value:
note to self:

select ent.name, ens.lc_name || '/' || en.filename, en.name, frs.lc_name || '/' || fr.filename, frt.name, fr.name from section ens, template ent, page en, template frt, page fr, section frs, item_relationship ir where ens.id=en.section_id and frs.id=fr.section_id and ir.DEPENDENT_ITEM_ID = fr.item_id and ir.item_id = en.item_id and fr.channel = 'french' and en.template_id != fr.template_id and ir.relationship_type_id != 1 and ent.id = en.template_id and frt.id = fr.template_id
/


var en = Page.get("visiting/getting-to-the-games/index.php");
var fr = Page.get("fr/visiting/getting-to-the-games/index.php");

var frTmp = new Page({
  template: "Travel hub (l1)",
  path: "temporary/tmp.php",
  name: "tmp"
});

frTmp.xml = fr.xml;
fr.xml.@state = 'In progress';

frTmp.save();

fr.replaceWith(frTmp);
