/* Added by Kieran */
/* Make them be in the same order as the venue metadata tags */
var newarr = [];
for (var i=0; i < venues.length; i++) {
    var venue = venues[i];
    var order = 0;
    for each (var venueName in [venueName1,venueName2,venueName3,venueName4,venueName5,venueName6]) {
        order++;
        if (venueName == 'Hadleigh Farm') {venueName = 'Hadleigh Farm, Essex'}
        if (venueName==venue.name) {
            venue.order = order;
            break;
        }
    }
    newarr.push(venue);
}
newarr.sort(function (a,b) {
  return a.order - b.order;
});
venues = newarr;
/* END - Added by Kieran */
